mapper.delete:
delete from coursestudent
where courseid = #{courseId,jdbcType=INTEGER}
and
studentid=#{studentId,jdbcType=INTEGER}

mapper.insert:
insert into coursestudent (id, courseid, studentid,archive
  )
values (#{id,jdbcType=INTEGER}, #{courseid,jdbcType=INTEGER}, #{studentid,jdbcType=INTEGER},0
  )

mapper.update:
update coursestudent
set archive = 1
where courseid = #{courseId,jdbcType=INTEGER}
and
 studentid = #{studentId,jdbcType=INTEGER}

update coursestudent
set archive = 1
where courseid = #{courseId,jdbcType=INTEGER}

update coursestudent
set courseid = #{courseid,jdbcType=INTEGER},
  studentid = #{studentid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
